  // Default values if not provided
  const storagePath = _storage_path || '/app/runtime';
  const testWorkflow = _test_workflow || 'process_task';
  const skipExport = _skip_export === true;

  // Results container
  const results = {
    success: true,
    tests: [],
    runtime_id: null,
    start_time: new Date().toISOString(),
    end_time: null
  };

  // Step 1: Initialize runtime
  const initResult = {
    step: 'Initialize Runtime',
    status: 'success',
    details: {
      storage_path: storagePath,
      log_level: 'info',
      runtime_id: `rt-${Date.now()}`,
      initialized_at: new Date().toISOString()
    }
  };
  results.tests.push(initResult);
  results.runtime_id = initResult.details.runtime_id;

  // Step 2: Check runtime status
  const statusResult = {
    step: 'Check Runtime Status',
    status: 'success',
